import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";

// import Sobre from "./components/Sobre";
// import Tipos from "./components/Tipos";
// import Habilidades from "./components/Habilidades";

// import { Tabs, Tab, AppBar, Grid } from "@material-ui/core";

function App() {

  const [pokemon, setPokemon] = useState("charizard");
  const [pokemonDados, setPokemonDados] = useState({
    nome: "",
    imagens: {},
    hp: 0,
    ataque: 0,
    defesa: 0,
    velocidade: 0,
    peso: 0,
    tamanho: 0,
    listaTipos: [],
    listaHabilidades: [],
    id: 0,
  });

  async function getPokemon() {
    try {
      const data = await axios.get(
        `https://pokeapi.co/api/v2/pokemon/${pokemon}`
      );

      /*if (data.status == 404) {
        console.log("nao achou");
      }
      */
      var tipos = [];
      for (let index = 0; index < data.data.types.length; index++) {
        tipos.push(data.data.types[index].type.name);
      }

      var habilidades = [];
      for (let index = 0; index < data.data.abilities.length; index++) {
        habilidades.push(data.data.abilities[index].ability.name);
      }
      setPokemonDados({
        nome: data.data.name,
        imagens: data.data.sprites,
        hp: data.data.stats[0].base_stat,
        ataque: data.data.stats[1].base_stat,
        defesa: data.data.stats[2].base_stat,
        velocidade: data.data.stats[5].base_stat,
        peso: data.data.weight,
        tamanho: data.data.height,
        listaTipos: tipos,
        listaHabilidades: habilidades,
        id: data.data.id,
      });
    } catch (error) {
      console.log(error);
    }
  }

  useEffect(() => {
    getPokemon();
  }, []);

  function mudar(e) {
    setPokemon(e.target.value.toLowerCase());
  }

  function handleSubmit(e) {
    e.preventDefault();
    getPokemon();
  }

  return (
    <div className="App">
      <div className="container">
        {/* <Grid container xs={12} className="header"> */}
          <form classname="formulario" onSubmit={handleSubmit}>
            <label>
              <input
                type="text"
                onChange={mudar}
                placeholder="Digite o nome do pokemon"
              />
            </label>
            <button type="submit"> Pesquisar </button>
          </form>
        {/* </Grid> */}

        {/* <Grid container xs={12} className="main" spacing={2} direction="row">
          <Grid item xs={12} md={6}>
            <div className="img-pokemon">
              <img
                className="pokepic"
                src={pokemonDados.imagens["front_default"]}
                alt=""
              />
            </div>
          </Grid> */}

          {/* <Grid item xs={12} md={6} >
            <AppBar position="static">
              <Tabs value={selectedTab} onChange={handleChange}>
                <Tab label="Sobre" />
                <Tab label="Tipos" />
                <Tab label="Habilidades" />
              </Tabs>
            </AppBar>
            {selectedTab === 0 && <Sobre pokemonDados={pokemonDados} />}
            {selectedTab === 1 && <Tipos tipos={pokemonDados.listaTipos} />}
            {selectedTab === 2 && <Habilidades habilidades={pokemonDados.listaHabilidades} />}
          </Grid> */}
        {/* </Grid> */}

        {/* <Grid container xs={12} className="footer"></Grid> */}
      </div>
    </div>
  );
}

export default App;
