import React from "react";
import App from "../App";
const pokemonData = props;
export default class Infos extends App {
  render() {
    return (
      pokemonData && (
        <div className="Infos">
          {Object.keys(pokemonData.images).length != 0 && (
            <img
              className="imagem"
              src={pokemonData.images.front_default}
            ></img>
          )}
          <div className="infosPokemon">
            <div className="info" id="namePokemon">
              <span>Nome: </span>
              <span style={{ textTransform: "capitalize" }}>
                {pokemonData.name}
              </span>
            </div>
            <div className="info">
              <span>HP: </span>
              <span>{pokemonData.hp}</span>
            </div>
            <div className="info">
              <span>Ataque: </span>
              <span>{pokemonData.attack}</span>
            </div>
            <div className="info">
              <span>Defesa: </span>
              <span>{pokemonData.defense}</span>
            </div>
            <div className="info">
              <span>Velocidade: </span>
              <span>{pokemonData.velocity}</span>
            </div>
            <div className="info">
              <span>Peso: </span>
              <span>{pokemonData.weight}</span>
            </div>
            <div className="info">
              <span>Tamanho: </span>
              <span>{pokemonData.size}</span>
            </div>
            <div className="info">
              <span>Habilidades: </span>
              {pokemonData.arrayAbilities.map((ability) => (
                <div style={{ textTransform: "capitalize" }}>
                  <br />
                  <div
                    onClick={() => props.getByAbility(ability.url)}
                    key={ability.url}
                  >
                    {ability.name}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )
    );
  }
}
