import "./App.css";
import React, { useState, useEffect, Item } from "react";
import axios from "axios";
import { Alert, AlertTitle } from "@mui/material";

function App() {
  const [pokemon, setPokemon] = useState();
  const [pokemonData, setPokemonData] = useState({
    name: "",
    images: {},
    hp: 0,
    attack: 0,
    defense: 0,
    velocity: 0,
    peso: 0,
    size: 0,
    arrayTypes: [],
    arrayAbilities: [],
    id: 0,
  });
  const [pokemonImages, setPokemonImages] = useState();
  let pokemonSubmited = false;

  async function getPokemon() {
    try {
      const data = await axios.get(
        `https://pokeapi.co/api/v2/pokemon/${pokemon}`
      );
      pokemonSubmited = true;
      this.forceUpdate();
      console.log("pokemonSubmited", pokemonSubmited);
      var types = [];
      for (let index = 0; index < data.data.types.length; index++) {
        let objTypes = {
          name: data.data.types[index].type.name,
          url: data.data.types[index].type.url,
        };
        types.push(objTypes);
      }

      var abilities = [];
      for (let index = 0; index < data.data.abilities.length; index++) {
        let objTypes = {
          name: data.data.abilities[index].ability.name,
          url: data.data.abilities[index].ability.url,
        };
        abilities.push(objTypes);
      }
      setPokemonData({
        name: data.data.name,
        images: data.data.sprites,
        hp: data.data.stats[0].base_stat,
        attack: data.data.stats[1].base_stat,
        defense: data.data.stats[2].base_stat,
        velocity: data.data.stats[5].base_stat,
        weight: data.data.weight,
        size: data.data.height,
        arrayTypes: types,
        arrayAbilities: abilities,
        id: data.data.id,
      });
      console.log("ab", pokemonData.arrayAbilities);
      console.log("typ", pokemonData.arrayTypes);
      // let images = Object.values(pokemonData.images);

      // setPokemonImages(images);
    } catch (error) {
      <Alert severity="error">
        <AlertTitle>Erro</AlertTitle>
        Pokemon não encontrado
      </Alert>;
    }
  }

  async function getByAbility() {
    try {
      const data = await axios.get(
        `https://pokeapi.co/api/v2/pokemon/${pokemon}`
      );

      var types = [];
      for (let index = 0; index < data.data.types.length; index++) {
        types.push(data.data.types[index].type.name);
      }

      var abilities = [];
      for (let index = 0; index < data.data.abilities.length; index++) {
        abilities.push(data.data.abilities[index].ability.name);
      }
      setPokemonData({
        name: data.data.name,
        images: data.data.sprites,
        hp: data.data.stats[0].base_stat,
        attack: data.data.stats[1].base_stat,
        defense: data.data.stats[2].base_stat,
        velocity: data.data.stats[5].base_stat,
        weight: data.data.weight,
        size: data.data.height,
        arrayTypes: types,
        arrayAbilities: abilities,
        id: data.data.id,
      });

      // let images = Object.values(pokemonData.images);

      // setPokemonImages(images);
    } catch (error) {
      <Alert severity="error">
        <AlertTitle>Erro</AlertTitle>
        Pokemon não encontrado
      </Alert>;
    }
  }
  async function getByType() {
    try {
      const data = await axios.get(
        `https://pokeapi.co/api/v2/pokemon/${pokemon}`
      );

      var types = [];
      for (let index = 0; index < data.data.types.length; index++) {
        types.push(data.data.types[index].type.name);
      }

      var abilities = [];
      for (let index = 0; index < data.data.abilities.length; index++) {
        abilities.push(data.data.abilities[index].ability.name);
      }
      setPokemonData({
        name: data.data.name,
        images: data.data.sprites,
        hp: data.data.stats[0].base_stat,
        attack: data.data.stats[1].base_stat,
        defense: data.data.stats[2].base_stat,
        velocity: data.data.stats[5].base_stat,
        weight: data.data.weight,
        size: data.data.height,
        arrayTypes: types,
        arrayAbilities: abilities,
        id: data.data.id,
      });

      // let images = Object.values(pokemonData.images);

      // setPokemonImages(images);
    } catch (error) {
      <Alert severity="error">
        <AlertTitle>Erro</AlertTitle>
        Pokemon não encontrado
      </Alert>;
    }
  }

  useEffect(() => {
    getPokemon();
  }, []);

  function pokemonInputChange(e) {
    setPokemon(e.target.value.toLowerCase());
  }

  function handleSubmit(e) {
    e.preventDefault();
    getPokemon();
  }

  return (
    <div className="App">
      <div className="container">
        {/* <Grid container xs={12} className="header"> */}
        <h1 className="title">PokePage</h1>
        <form className="formulario" onSubmit={handleSubmit}>
          <label>
            <input
              type="text"
              onChange={pokemonInputChange}
              placeholder="Digite o nome do pokemon"
            />
          </label>
          <button type="submit"> Pesquisar </button>
        </form>
        {pokemonSubmited && (
          <div>
            {Object.keys(pokemonData.images).length != 0 && (
              <img
                className="imagem"
                src={pokemonData.images.front_default}
              ></img>
            )}
            <div className="infosPokemon">
              <div className="info" id="namePokemon">
                <span>Nome: </span>
                <span style={{ textTransform: "capitalize" }}>
                  {pokemonData.name}
                </span>
              </div>
              <div className="info">
                <span>HP: </span>
                <span>{pokemonData.hp}</span>
              </div>
              <div className="info">
                <span>Ataque: </span>
                <span>{pokemonData.attack}</span>
              </div>
              <div className="info">
                <span>Defesa: </span>
                <span>{pokemonData.defense}</span>
              </div>
              <div className="info">
                <span>Velocidade: </span>
                <span>{pokemonData.velocity}</span>
              </div>
              <div className="info">
                <span>Peso: </span>
                <span>{pokemonData.weight}</span>
              </div>
              <div className="info">
                <span>Tamanho: </span>
                <span>{pokemonData.size}</span>
              </div>
              <div className="info">
                <span>Ability: </span>
                {pokemonData.arrayAbilities.items?.map((item, index) => (
                  <Item key={index} item={item} />
                ))}
              </div>
            </div>
          </div>
        )}
        {/* </Grid> */}

        {/* <Grid container xs={12} className="main" spacing={2} direction="row">
          <Grid item xs={12} md={6}>
            <div className="img-pokemon">
              <img
                className="pokepic"
                src={pokemonData.images["front_default"]}
                alt=""
              />
            </div>
          </Grid> */}

        {/* <Grid item xs={12} md={6} >
            <AppBar position="static">
              <Tabs value={selectedTab} onChange={handleChange}>
                <Tab label="Sobre" />
                <Tab label="Tipos" />
                <Tab label="Habilidades" />
              </Tabs>
            </AppBar>
            {selectedTab === 0 && <Sobre pokemonData={pokemonData} />}
            {selectedTab === 1 && <Tipos types={pokemonData.arrayTypes} />}
            {selectedTab === 2 && <Habilidades abilities={pokemonData.arrayAbilities} />}
          </Grid> */}
        {/* </Grid> */}

        {/* <Grid container xs={12} className="footer"></Grid> */}
      </div>
    </div>
  );
}

export default App;
